func (uis *UplItemSelect) sqlScan(ctx context.Context, v interface{}) error {
	for _, f := range uis.fields {
		if !uplitem.ValidColumn(f) {
			return &ValidationError{Name: f, err: fmt.Errorf("invalid field %q for selection", f)}
		}
	}
	rows := &sql.Rows{}
	query, args := uis.sqlQuery().Query()
	if err := uis.driver.Query(ctx, query, args, rows); err != nil {
		return err
	}
	defer rows.Close()
	return sql.ScanSlice(rows, v)
}

func (uis *UplItemSelect) sqlQuery() sql.Querier {
	selector := uis.sql
	selector.Select(selector.Columns(uis.fields...)...)
	return selector
}
